SELECT count(*)
FROM consultant_supervisor_order WHERE consultant_supervisor_order.is_delete=0 AND consultant_supervisor_order.consultant_id=#{consultantId}
AND consultant_supervisor_order.order_status in
#{type}

SELECT _c.id, _c.order_no AS "orderNO", _c.order_status AS "orderStatus", _c.supervisor_id AS "consultantId", user.real_name AS "realName", CONCAT(user.HEAD_IMG,user.head_img_size) AS "headImg", user.head_img_size AS "headImgSize", consultant_supervisor_order_details.mall_trad_order_no AS "mallTradOrderNo"
FROM( SELECT consultant_supervisor_order.id, consultant_supervisor_order.order_no, consultant_supervisor_order.order_status, consultant_supervisor_order.supervisor_id FROM consultant_supervisor_order WHERE consultant_supervisor_order.is_delete=0 AND consultant_supervisor_order.consultant_id=#{consultantId}
AND consultant_supervisor_order.order_status in
#{type}
ORDER BY consultant_supervisor_order.id DESC LIMIT #{pageNum},#{pageSize} ) _c LEFT JOIN consultant_supervisor_order_details ON order_no=consultant_supervisor_order_details.supervisor_order_no LEFT JOIN mall_trade_order ON consultant_supervisor_order_details.mall_trad_order_no=mall_trade_order.order_no LEFT JOIN user ON _c.supervisor_id=user.id
ORDER BY _c.id DESC

SELECT count(*)
FROM consultant_supervisor_order WHERE consultant_supervisor_order.is_delete=0 AND consultant_supervisor_order.supervisor_id=#{supervisorId}
AND consultant_supervisor_order.order_status in
#{type}

SELECT _c.id, _c.order_no AS "orderNO", _c.order_status AS "orderStatus", _c.consultant_id AS "consultantId", user.real_name AS "realName", CONCAT(user.HEAD_IMG,user.head_img_size) AS "headImg", user.head_img_size AS "headImgSize", consultant_supervisor_order_details.mall_trad_order_no AS "mallTradOrderNo"
FROM( SELECT consultant_supervisor_order.id, consultant_supervisor_order.order_no, consultant_supervisor_order.order_status, consultant_supervisor_order.consultant_id
FROM consultant_supervisor_order
WHERE consultant_supervisor_order.is_delete=0 AND consultant_supervisor_order.supervisor_id=#{supervisorId}
AND consultant_supervisor_order.order_status in
#{type}
ORDER BY consultant_supervisor_order.id DESC LIMIT #{pageNum},#{pageSize}
)_c LEFT JOIN consultant_supervisor_order_details ON order_no=consultant_supervisor_order_details.supervisor_order_no LEFT JOIN mall_trade_order ON consultant_supervisor_order_details.mall_trad_order_no=mall_trade_order.order_no LEFT JOIN user ON _c.consultant_id=user.id ORDER BY _c.id DESC

SELECT consultant_supervisor_order.order_no AS "orderNo", consultant_supervisor_order.pay_time AS "createTime", consultant_supervisor_order.proportion AS "proportion", consultant_supervisor_order.order_status AS "orderStatus", 6 AS "workType", consultant_supervisor_order.goods_amount AS "goodsAmount", "æ— " AS "coupon", consultant_supervisor_order.order_amount AS "orderAmount", consultant_supervisor_order_details.mall_trad_order_no AS "mallTradOrderNo", user.ID AS "consultantId", user.`easemob_id` AS "easemobId", CONCAT(user.HEAD_IMG,user.head_img_size) AS "headImg", user.head_img_size AS "headImgSize", user.real_name AS "realName" FROM
consultant_supervisor_order,consultant_supervisor_order_details,user,mall_trade_order
WHERE consultant_supervisor_order.is_delete=0 AND user.id=consultant_supervisor_order.consultant_id AND consultant_supervisor_order.order_no=consultant_supervisor_order_details.supervisor_order_no AND consultant_supervisor_order_details.mall_trad_order_no=mall_trade_order.order_no AND consultant_supervisor_order.order_no=#{orderNo}

SELECT consultant_supervisor_order.id, consultant_supervisor_order.order_no AS "orderNo", consultant_supervisor_order.pay_time AS "payTime", user.real_name AS "consultantRealName", _user.real_name AS "supervisorRealName", consultant_supervisor_order.pay_status AS "payStatus", consultant_supervisor_order.order_status AS "orderStatus", consultant_supervisor_order.goods_amount AS "goodsAmount", consultant_supervisor_order.order_amount AS "orderAmount", consultant_supervisor_order.proportion AS "proportion" FROM
consultant_supervisor_order,user,user _user
WHERE consultant_supervisor_order.is_delete=0 AND user.id=consultant_supervisor_order.consultant_id AND _user.id=consultant_supervisor_order.supervisor_id
AND user.id in
#{userId}
AND user.user_name=#{consultantUserName}
AND _user.user_name=#{userName}
AND consultant_supervisor_order.pay_time >=#{orderTime}
ORDER BY consultant_supervisor_order.id DESC
